const data = JSON.parse($input.item.json.message || '{}');

// Output the parsed data to continue the workflow
return {
  json: data
};

const embeddingResponse = $input.item.json.embedding;

// Extract just the values array from the embedding object
const embedding = embeddingResponse && embeddingResponse.values 
  ? embeddingResponse.values   // If it has a 'values' property, use that
  : embeddingResponse;         // Otherwise use as is

return {
  json: {
    ...$json,
    embedding
  }
};

// The DB results are now in the 'body' property because of the merge.
const results = $input.item.body || [];
const hasResults = results.length > 0;
const goodResults = results.filter(r => r.similarity > 0.8);
const hasQualityResults = goodResults.length >= 3;

const resultsByType = {};
if (hasResults) {
  results.forEach(item => {
    const docType = item.doc_type || 'other';
    if (!resultsByType[docType]) {
      resultsByType[docType] = [];
    }
    resultsByType[docType].push(item);
  });
}

// Now, we correctly merge the original data with the new evaluation.
return {
  json: {
    ...$input.item.json, // Pass through all original data
    searchResults: results,
    resultsByType,
    hasQualityResults
  }
};

const incomingData = $input.item.json;
const results = incomingData.organic_results || [];
const webResults = results.filter(r => !r.link.includes('youtube.com'));
const urls = webResults.length > 0 ? webResults.map(r => r.link) : results.map(r => r.link);

const batchId = Date.now().toString();

return {
  json: {
    ...incomingData, // Pass through all original data
    urls: [...new Set(urls)].slice(0, 5),
    urlCount: Math.min([...new Set(urls)].slice(0, 5).length, 5),
    batchId
  }
};

