try {
  const resume = $json.resume || '';
  const job_title = $json.job_title || '';
  const job_description = $json.job_description || '';
  const company = $json.company || '';
  const rawResults = $json.searchResults || [];

  // Defensive: ensure array
  const results = Array.isArray(rawResults) ? rawResults : [];

  // Sort by similarity desc and take top N (choose 5)
  const topN = 5;
  const top = results
    .slice()
    .sort((a,b) => (b.similarity || 0) - (a.similarity || 0))
    .slice(0, topN);

  // Build a compact context block: each item on its own paragraph
  const contexts = top.map((r, i) => {
    const type = r.doc_type || 'context';
    const content = (r.content || '').trim();
    // Limit snippet length to 1000 chars for prompt safety
    const snippet = content.length > 1000 ? content.slice(0,1000) + '...' : content;
    return `${i+1}. [${type}] ${snippet}`.trim();
  });

  // Build the enhanced prompt (clean)
  const enhanced_prompt = [
